mapper.select:
SELECT USERNAME
FROM USERS
WHERE USER_NM = #{userNm} AND EMAIL = #{email}

SELECT AE_ID, USERNAME, EMAIL
FROM USERS
WHERE USERNAME = #{usernameOrEmail} OR EMAIL = #{usernameOrEmail}

mapper.update:
UPDATE USERS
SET PASSWORD = #{password}
WHERE USERNAME = #{username}

mapper.select:
SELECT USER_ID, USERNAME, PASSWORD, EMAIL, USER_NM as name, PHONE, ADDRESS
FROM USERS
WHERE USER_NM = #{name} AND USERNAME = #{username} AND EMAIL = #{email}

mapper.update:
UPDATE USERS
SET MUST_CHANGE_PASSWORD = #{mustChangePassword}
WHERE USERNAME = #{username}

mapper.select:
SELECT USER_ID, USERNAME, PASSWORD, EMAIL, USER_NM as name, PHONE, ADDRESS
FROM USERS
WHERE USER_ID = #{userId}
